select DISTINCT id,businessKey,code,createOrgId,createOrgName,createUserCode,createUserId,createUserName,delegationState,duration,endActivityId,endActivityName,endTime,executionId,iscg,previousAssignee,previousAssigneeDate,previousAssigneeName,previousAssigneeOrgId,previousAssigneeOrgName,previousAssigneeUniqueCode,processDefinitionId,processDefinitionKey,processDefinitionName,processInstanceId,startActivityId,startActivityName,startTime,taskAssignee,taskId,taskKey,taskName,taskOwner,taskStartTime,title,createTime,updateTime,lastTimeStamp,demandUserId,demandOrgId,act_parentId,enabled,removed, isStop,chargeUserCode,developUserCode from (SELECT s.*,a.completeTime FROM act_business_status s,act_task_assigne a , sys_user u WHERE s.processInstanceId =a.processInstanceId AND s.demandUserId = u.id AND s.iscg=0 AND s.code like CONCAT('%','${code}','%' ) AND s.title like CONCAT('%','${title}','%' ) AND s.code like CONCAT('%','${processDefinitionKeys}','%' ) and s.startTime >= #{startTime} and s.startTime <= #{endTime} and s.endTime is null and s.endTime is not null and s.isstop is null and s.isstop = 1 and u.username like CONCAT("%",#{demandUserName},"%") and s.demandOrgId IN
#{item}
AND s.enabled=1 AND a.completeTime IS NOT NULL AND (a.owner=#{uniqueCode} OR a.assignee=#{uniqueCode}) ORDER BY a.completeTime DESC) a

